with open('meal_data.csv', mode='w', newline='') as file:
    writer = csv.writer(file)
    writer.writerow(['Meal_id', 'Meal_type_id', 'Person_id', 'Meal_time', 'Meal_date', 'Entry_type', 'Restaurant_id', 'Food_id'])

    for meal_id in range(1, 500001):
        # meal_type_id = random.choice(meal_types)
        person_id = random.choice(person_ids)
        meal_time = datetime.datetime.now() - datetime.timedelta(days=random.randint(1, 365*3), minutes=random.randint(0, 1439))
        meal_time_str = meal_time.strftime('%H:%M:%S.%f')
        meal_date_str = meal_time.strftime('%Y-%m-%d')
        entry_type = random.choice(entry_types)
        if entry_type == 'Track':
            f_r = random.choice(res_food_meal)
            restaurant_id = f_r[0]
            food_id = f_r[1]
            meal_type_id = f_r[2]
        else:
            restaurant_id = ''
            food_id = ''
            meal_type_id = random.choice(meal_types)
        writer.writerow([meal_id, meal_type_id, person_id, meal_time_str, meal_date_str, entry_type, restaurant_id, food_id])
